import React, { useRef, useState } from "react";
import FormInput from "../formInput/FormInput";
import "./form.css";

export default function Form() {
  //the start value of the state is empty
  //   const [username, setUsername] = useState("");
  //when using set state every time the input get updated the the hole function get re-render which is not good always
  //so instate of using (useState) we can use (useRef) hook so the input will not re-render each time it's been updated
  //   const usernameRef = useRef();
  //   const handleSubmit = (e) => {
  //     e.preventDefault();
  //     console.log(usernameRef.current.value);
  //   };

  // another thing you can use is the formData() method
  const handleSubmit = (e) => {
    e.preventDefault();
    const data = new FormData(e.target); //in order for the form to know the target we will use the name attribute is the input element

    //the inputs fields data we want is now in the entries of dataObject, and to reach them we will use object.fromEntries()
    console.log(data);
    console.log(Object.fromEntries(data.entries()));
  };

  console.log("re-render");
  return (
    <form action="POST" onSubmit={handleSubmit}>
      <FormInput name="username" placeholder="username" />
      <FormInput name="email" placeholder="Email" />
      <FormInput name="fullname" placeholder="full Name" />
      <FormInput name="password" placeholder="password" />
      <button type="submit">Submit</button>
    </form>
  );
}
