import React, { useEffect, useMemo } from "react";
import "./cursor.css";

export default function Cursor() {
  const addElements = useMemo(() => {
    const cursorContainer = document.querySelector(".cursorContainer");
    console.log(cursorContainer.children.length);
    for (let i = 1; i < 22; i++) {
      cursorContainer.innerHTML += `<div class="cursor eg${i}"></div>`;
    }
  }, []);
  useEffect(() => {
    addElements();
  }, []);
  return (
    <div className="wrapper">
      <div className="cursorContainer"></div>
    </div>
  );
}
