import React, { useEffect, useState } from "react";
import "./gradient.css";
import {
  ArrowBack,
  ArrowForward,
  ArrowUpward,
  ArrowDownward,
} from "@mui/icons-material";

export default function Gradient() {
  const [values, setValues] = useState({
    name: "left",
    color1: "#356ed5",
    color1Percent: 20,
    color2: "#00ffad",
    color2Percent: 80,
  });

  useEffect(() => {
    const activeHandler = (e) => {
      //set arrows active className
      const arrowIcons = document.querySelectorAll(".arrowIcon");
      arrowIcons.forEach((icon) => {
        icon.classList.remove("active");
        icon.addEventListener("click", (e) => {
          e.target.classList.add("active");
          setValues({
            ...values,
            [e.target.name]: e.target.getAttribute("value"),
          });
        });
      });
      console.log(values);
    };
  }, [values]);

  return (
    <div className="wrapper">
      <div className="showGradient">
        <div className="gradientResult mb-5">
          <div className="gradientBox"></div>
        </div>
        <div className="gradientInput">
          <div className="gradientDirection">
            <h2>Gradient Direction</h2>
            <div className="arrows">
              <ArrowUpward className="arrowIcon" name="top" value="top" />
              <ArrowForward className="arrowIcon" name="right" value="right" />
              <ArrowDownward
                className="arrowIcon"
                name="bottom"
                value="bottom"
              />
              <ArrowBack className="arrowIcon" name="left" value="left" />
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
