import React, { useEffect, useState } from "react";
import "./boxshadow.css";

export default function BoxShadow() {
  const [offsetX, setOffsetX] = useState(0);
  const [offsetY, setOffsetY] = useState(5);
  const [blur, setBlur] = useState(15);
  const [spread, setSpread] = useState(0);
  const [color, setColor] = useState("#00000059");

  useEffect(() => {
    let result = document.querySelector(".result");
    result.style.boxShadow = `${offsetX}px ${offsetY}px ${blur}px ${spread}px ${color}`;
  }, [offsetX, offsetY, color, spread, blur]);

  return (
    <div className="wrapper">
      <div className="container">
        <div className="show-gradient">
          <div className="result">
            <span>click to copy</span>
          </div>
        </div>
        <div className="gradient-input">
          <div className="offset-x mb-3">
            <label htmlFor="" className="form-label">
              Offset-X
            </label>
            <input
              type="range"
              name=""
              id=""
              min={0}
              max={30}
              value={offsetX}
              onChange={(e) => setOffsetX(e.target.value)}
            />
            <span className="showValue">{offsetX} px</span>
          </div>
          <div className="offset-y mb-3">
            <label htmlFor="" className="form-label">
              Offset-Y
            </label>
            <input
              type="range"
              name=""
              id=""
              min={0}
              max={30}
              value={offsetY}
              onInput={(e) => setOffsetY(e.target.value)}
            />
            <span className="showValue">{offsetY} px</span>
          </div>
          <div className="Blur mb-3">
            <label htmlFor="" className="form-label">
              Blur
            </label>
            <input
              type="range"
              name=""
              id=""
              min={0}
              max={30}
              value={blur}
              onInput={(e) => setBlur(e.target.value)}
            />
            <span className="showValue">{blur} px</span>
          </div>
          <div className="Spread mb-3">
            <label htmlFor="" className="form-label">
              Spread
            </label>
            <input
              type="range"
              name=""
              id=""
              min={0}
              max={30}
              value={spread}
              onInput={(e) => setSpread(e.target.value)}
            />
            <span className="showValue">{spread} px</span>
          </div>
          <div className="color mb-3">
            <label htmlFor="" className="form-label">
              Color
            </label>
            <input
              type="color"
              name=""
              id=""
              value={color}
              onInput={(e) => setColor(e.target.value)}
            />
          </div>
        </div>
      </div>
    </div>
  );
}
