import React, { useEffect, useState } from "react";
import "./gradient.css";
import {
  ArrowBack,
  ArrowForward,
  ArrowUpward,
  ArrowDownward,
} from "@mui/icons-material";

export default function Gradient() {
  const [values, setValues] = useState({
    name: "left",
    color1: "#356ed5",
    color1Percent: 20,
    color2: "#00ffad",
    color2Percent: 80,
  });

  const activeHandler = (e) => {
    //set arrows active className
    const arrowIcons = document.querySelectorAll(".arrowIcon");
    arrowIcons.forEach((icon) => {
      icon.classList.remove("active");
      icon.addEventListener("click", (e) => {
        e.target.classList.add("active");
        setValues({
          ...values,
          [e.target.getAttribute("name")]: e.target.getAttribute("value"),
        });
      });
    });
  };
  console.log(values);

  return (
    <div className="wrapper">
      <div className="showGradient">
        <div className="gradientResult mb-5">
          <div className="gradientBox"></div>
        </div>
        <div className="gradientInput">
          <div className="gradientDirection">
            <h2>Gradient Direction</h2>
            <div className="arrows">
              <ArrowUpward
                className="arrowIcon"
                name="name"
                value="top"
                onClick={activeHandler}
              />
              <ArrowForward
                className="arrowIcon"
                name="name"
                value="right"
                onClick={activeHandler}
              />
              <ArrowDownward
                className="arrowIcon"
                name="name"
                value="bottom"
                onClick={activeHandler}
              />
              <ArrowBack
                className="arrowIcon"
                name="name"
                value="left"
                onClick={activeHandler}
              />
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
