import React, { useEffect, useState } from "react";
import "./gradient.css";
import {
  ArrowBack,
  ArrowForward,
  ArrowUpward,
  ArrowDownward,
} from "@mui/icons-material";

export default function Gradient() {
  const [values, setValues] = useState({
    dir: "left",
    color1: "#356ed5",
    color1Percent: 20,
    color2: "#00ffad",
    color2Percent: 80,
  });

  const activeHandler = (e) => {
    //set arrows active className
    const arrowIcons = document.querySelectorAll(".arrowIcon");
    arrowIcons.forEach((icon) => {
      icon.classList.remove("active");
    });
    e.target.classList.add("active");
    setValues({
      ...values,
      [e.target.getAttribute("name")]: e.target.getAttribute("value"),
    });
    console.log(e.target.name, e.target.value);
  };

  return (
    <div className="wrapper">
      <div className="showGradient">
        <div className="gradientResult mb-5">
          <div className="gradientBox"></div>
        </div>
        <div className="gradientInput">
          <div className="gradientDirection">
            <h2>Gradient Direction</h2>
            <div className="arrows">
              <button name="dir" value="top" onClick={activeHandler}>
                <ArrowUpward className="arrowIcon" />
              </button>
              <button name="dir" value="right" onClick={activeHandler}>
                <ArrowForward className="arrowIcon" name="dir" value="right" />
              </button>
              <button name="dir" value="bottom" onClick={activeHandler}>
                <ArrowDownward className="arrowIcon" />
              </button>
              <button name="dir" value="left" onClick={activeHandler}>
                <ArrowBack className="arrowIcon" />
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
